import { useEffect, useState } from 'react';
import './App.css';
import axios from 'axios';

interface Data {
  name: string;
  age: number;
  job: string;
}
const express = require('express');
const cors = require('cors');

function App() {
  const [data, setData] = useState<Data | null>(null);

  useEffect(() => {
    axios.get('http://localhost:5000/api/data')
      .then((response) => {setData(response.data)})
      .catch((error) => {
        console.error(error);
        setData(null); // エラー時にデータをnullに設定
      });
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <div className="font">
          {data ? (
            <div>
              <p>Name: {data.name}</p>
              <p>Age: {data.age}</p>
              <p>Job: {data.job}</p>
            </div>
          ) : (
            <p>Loading...</p> // データがない場合はローディングテキストを表示
          )}
        </div>
      </header>
    </div>
  );
}

export default App;
