import React, { useState, useEffect } from 'react';

type Coordinate = [number, number];

// 方向を表す型（画像の各方向の座標）
type PhotoDirection = {
  [direction: string]: Coordinate;
};

// 画像データとその位置情報を含む型
type ImageData = {
  image1: string;
  image2: string;
  location: Coordinate;
  photo_direction: PhotoDirection;
};

const DataDisplay: React.FC = () => {
    const [data, setData] = useState<ImageData | null>(null);
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
      fetch('/home/kobayashi/StreetViewCreator/SVC/client/pseudo_data.json')
        .then(response => response.json())
        .then(data => setData(data as ImageData))
        .catch(error => {
          console.error('Error loading the data:', error);
          setError('Failed to load data');
        });
    }, []);

    if (error) {
      return <div>Error: {error}</div>;
    }

    if (!data) {
      return <div>Loading...</div>;
    }

    return (
      <div>
        <h1>Images and Directions</h1>
        <img src={data.image1} alt="Image 1" />
        <img src={data.image2} alt="Image 2" />
        <p>Location: X={data.location[0]}, Y={data.location[1]}</p>
        {Object.entries(data.photo_direction).map(([key, [x, y]]) => (
          <p key={key}>Direction {key}: X={x}, Y={y}</p>
        ))}
      </div>
    );
  };

  export default DataDisplay;
